#include <iostream>
#include "playing_field.h"

using namespace std;

Playing_field::Playing_field(){

}
Playing_field::~Playing_field(){

}
string Playing_field::welcome_field(){
    return line() + logo() + line();
}
string Playing_field::event_field(string question, string health_hero, string health_enemy, Enemy &enemy){
    return line() + logo() + line() + "\t\t\t\t\t\t\t\t\t\t" + health_board(health_hero, health_enemy) + "\n" + line() + screen_characters(enemy) + "\n" + line() + question;
}
string Playing_field::health_to_str(Character &ch){
    int health = ch.get_health();
    return to_string(health);
}
string Playing_field::health_board(string hero_health, string enemy_hearth){
    return "Hero's health: " + hero_health + " | " + "Enemy health: " + enemy_hearth;
}
